local a=require('data.min')local b=require('battery.min')local c=require('sprite.min')local d=require('code.min')local e=require('text_sprite_block.min')TEXT_SPRITE_BLOCK=0x20;CLEAR_MSG=0x10;a.parsers[TEXT_SPRITE_BLOCK]=e.parse_text_sprite_block;a.parsers[CLEAR_MSG]=d.parse_code;function app_loop()frame.display.text(" ",1,1)frame.display.show()local f=0;local g=0;while true do local h=a.process_raw_items()if h>0 then if a.app_data[TEXT_SPRITE_BLOCK]~=nil then local i=a.app_data[TEXT_SPRITE_BLOCK]for j,k in ipairs(i.sprites)do frame.display.bitmap(1,i.offsets[j].y+1,k.width,2^k.bpp,0,k.pixel_data)end;frame.display.show()g=frame.time.utc()end;if a.app_data[CLEAR_MSG]~=nil then frame.display.text(" ",1,1)frame.display.show()a.app_data[CLEAR_MSG]=nil end end;f=b.send_batt_if_elapsed(f,120)frame.sleep(0.1)if a.app_data[TEXT_SPRITE_BLOCK]~=nil and frame.time.utc()-g>10 then frame.display.text(" ",1,1)frame.display.show()a.app_data[TEXT_SPRITE_BLOCK]=nil end end end;app_loop()